mapper.select:
SELECT zz_sys_user.* FROM zz_sys_user_role, zz_sys_user
AND zz_sys_user_role.role_id = #{roleId} AND zz_sys_user_role.user_id = zz_sys_user.user_id
ORDER BY ${orderBy}

SELECT * FROM zz_sys_user
NOT EXISTS (SELECT * FROM zz_sys_user_role WHERE zz_sys_user_role.role_id = #{roleId} AND zz_sys_user_role.user_id = zz_sys_user.user_id)
ORDER BY ${orderBy}

SELECT zz_sys_user.* FROM zz_sys_data_perm_user, zz_sys_user
AND zz_sys_data_perm_user.data_perm_id = #{dataPermId} AND zz_sys_data_perm_user.user_id = zz_sys_user.user_id
ORDER BY ${orderBy}

SELECT * FROM zz_sys_user
NOT EXISTS (SELECT * FROM zz_sys_data_perm_user WHERE zz_sys_data_perm_user.data_perm_id = #{dataPermId} AND zz_sys_data_perm_user.user_id = zz_sys_user.user_id)
ORDER BY ${orderBy}
